sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }


   //Type appropriate comment here, and begin script below
   var getdatafromajax =  new GlideAjax ('LoadStatevalue');
	getdatafromajax.addParam('sysparm_name', 'loadvalue');
	getdatafromajax.addParam('sysparm_value', g_form.getValue('state'));
	getdatafromajax.getXML(populatevalueindesc);

	function populatevalueindesc(response){
		var statevalue = response.responseXML.documentElement.getAttribute("answer");

		g_form.setValue('short_description', statevalue );
	}
}
